package internal

import (
	"encoding/hex"
	"encoding/json"
	"log"
	"testing"

	"github.com/btcsuite/btcd/btcjson"
	"github.com/dabankio/wallet-core/bip44"
	"github.com/dabankio/wallet-core/core"
	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
)

var (
	testMnemonic = "lecture leg select like delay limit spread retire toward west grape bachelor"
	btc          = &BTC{}
	testNet      = true
)

func init() {
	var err error
	seed, err := core.NewSeedFromMnemonic(testMnemonic, "")
	btc, err = New(bip44.PathFormat, false, seed, ChainRegtest)
	if err != nil {
		log.Fatal(err)
	}
}

func TestBTC_DerivePrivateKey(t *testing.T) {
	pk, err := btc.DerivePrivateKey()
	assert.NoError(t, err)
	t.Log(pk)
}

func TestBTC_DerivePublicKey(t *testing.T) {
	pk, err := btc.DerivePublicKey()
	assert.NoError(t, err)
	t.Log(pk)
}

func TestBTC_DeriveAddress(t *testing.T) {
	pk, err := btc.DeriveAddress()
	assert.NoError(t, err)
	t.Log(pk)
}

func TestBTC_DecodeTx(t *testing.T) {
	msg, err := btc.DecodeTx("")
	require.NoError(t, err, "btc.DecodeTx")
	t.Log(msg)

	msg, err = btc.DecodeTx("")
	require.NoError(t, err, "btc.DecodeTx")
	t.Log(msg)
}

func TestBTC_Sign(t *testing.T) {
	t.Skip("历史测试") //该测试为历史测试数据，现在不能通过，但不影响代码可用性
	// ./cli-hotnode signrawtransactionwithkey "0200000001a798b1665c115e0208fc5a73aca05597703ce2f063e81f98002c6e2d521f862a0000000000ffffffff0240b564000000000017a91457af28534e50bf8f8f8aa022efabd4a74c02a3028780841e00000000001976a9140f4971a4af12a54b9951c56b92dbcfce2870270588ac00000000" '["cV4nrs2iHooPTayGs3zcUYKW4wyG4gLQrFhUYXZDNswN3CDeRaKN"]'  '[{"txid":"2a861f522d6e2c00981fe863f0e23c709755a0ac735afc08025e115c66b198a7","vout":0,"scriptPubKey":"a91457af28534e50bf8f8f8aa022efabd4a74c02a30287","redeemScript":"522103a46570124c8d97fd5425134239ffef051a28f2845a12e05fecc66fd699776cf32103b9fee9c62286eefd6052e86bbb00659e5f746b1d63f7c730539efcd3e5c831cb2103ce6b440f1f0f19b918a9cb5b4b36e92ccc52091d1c6df2f9f059a6445c3a312b53ae","witnessScript":"","amount":0}]'
	// 	02000000 01 a798b1665c115e0208fc5a73aca05597703ce2f063e81f98002c6e2d521f862a 00000000 b4   (180)        0047304402202abd11f3298e66a08061354ca34941a3343832a6b1d053e54b130608973acd86022044dad04d3ae0882ffe43aad3299ce1e2e308c2f1cefa1f306aefe135e3b4914d01 4c69  522103a46570124c8d97fd5425134239ffef051a28f2845a12e05fecc66fd699776cf32103b9fee9c62286eefd6052e86bbb00659e5f746b1d63f7c730539efcd3e5c831cb2103ce6b440f1f0f19b918a9cb5b4b36e92ccc52091d1c6df2f9f059a6445c3a312b 53ae     ffffffff 02  40b564000000000017a91457af28534e50bf8f8f8aa022efabd4a74c02a3028780841e00000000001976a9140f4971a4af12a54b9951c56b92dbcfce2870270588ac00000000
	// 02000000 01 a798b1665c115e0208fc5a73aca05597703ce2f063e81f98002c6e2d521f862a 00000000 b5   (181)        0047304402202abd11f3298e66a08061354ca34941a3343832a6b1d053e54b130608973acd86022044dad04d3ae0882ffe43aad3299ce1e2e308c2f1cefa1f306aefe135e3b4914d01 00 4c69  522103a46570124c8d97fd5425134239ffef051a28f2845a12e05fecc66fd699776cf32103b9fee9c62286eefd6052e86bbb00659e5f746b1d63f7c730539efcd3e5c831cb2103ce6b440f1f0f19b918a9cb5b4b36e92ccc52091d1c6df2f9f059a6445c3a312b 53ae  ffffffff 02  40b564000000000017a91457af28534e50bf8f8f8aa022efabd4a74c02a3028780841e00000000001976a9140f4971a4af12a54b9951c56b92dbcfce2870270588ac00000000
	// ver      in hash                                                             vout     sigScriptLen                                                                                                                                                                                                                                                                                                                                                                                       Sequence out

	//             210 len
	//             2 <Public Key A> <Public Key B> <Public Key C> 3 CHECKMULTISIG
	//             64 * 3 = 192

	// other RedeemScript:(210 len)        52 21 02e06a99f8b6ad7ed986a6d13429cced62c71127dff704ba21028640f816e4afd8 21 024078d3e4c3d14659c02ace5eb03c5c1ec041cc2a0ab4fb23e72f9955aef4b024 21 034f3ba1aeb2bba9481663986af5e480bd96e20a4b0ea66215d9aee914175786f7 53ae
	// pubks 02e06a99f8b6ad7ed986a6d13429cced62c71127dff704ba21028640f816e4afd8,024078d3e4c3d14659c02ace5eb03c5c1ec041cc2a0ab4fb23e72f9955aef4b024,034f3ba1aeb2bba9481663986af5e480bd96e20a4b0ea66215d9aee914175786f7

	msg := new(btcjson.SignRawTransactionCmd)
	// createhex
	msg.RawTx = "0200000001a798b1665c115e0208fc5a73aca05597703ce2f063e81f98002c6e2d521f862a0000000000ffffffff0240b564000000000017a91457af28534e50bf8f8f8aa022efabd4a74c02a3028780841e00000000001976a9140f4971a4af12a54b9951c56b92dbcfce2870270588ac00000000"
	msg.Inputs = &[]btcjson.RawTxInput{
		{
			Txid:         "2a861f522d6e2c00981fe863f0e23c709755a0ac735afc08025e115c66b198a7",
			Vout:         0,
			ScriptPubKey: "a91457af28534e50bf8f8f8aa022efabd4a74c02a30287",
			RedeemScript: "522103a46570124c8d97fd5425134239ffef051a28f2845a12e05fecc66fd699776cf32103b9fee9c62286eefd6052e86bbb00659e5f746b1d63f7c730539efcd3e5c831cb2103ce6b440f1f0f19b918a9cb5b4b36e92ccc52091d1c6df2f9f059a6445c3a312b53ae",
		},
	}
	data, err := json.Marshal(msg)
	assert.NoError(t, err)
	sign, err := btc.Sign(hex.EncodeToString(data), "cV4nrs2iHooPTayGs3zcUYKW4wyG4gLQrFhUYXZDNswN3CDeRaKN")
	assert.NoError(t, err)
	assert.Equal(t, "", sign)

	// sign once hex
	msg.RawTx = sign
	data, err = json.Marshal(msg)
	assert.NoError(t, err)
	sign, err = btc.Sign(hex.EncodeToString(data), "cQUzSaxub2pHRg418zCwqJWDhGfShn21kqbXq6UoPRFRKpZsdfHn")
	assert.NoError(t, err)
	assert.Equal(t, "", sign)
}
